//equivalente al main
document.addEventListener("DOMContentLoaded", () => {

  //elementi modificabili in ogni pagina (cosa cambiare)
  pageElements = [
    "allprova",
    "titolo",
    "sottotitolo",
    "animazione",
    "maxi-icona",
    "skyline_C_Roma_home_1",
    "skyline_B_Roma_home_1",
    "skyline_A_Roma_home_1"
  ];

  lat = 51.5085300; //default Londra
  long = 0.1257400; //default Londra

    // Handler when the DOM is fully loaded
    if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(function (position) {
            lat = position.coords.latitude;
            long = position.coords.longitude;
        }, function () {
        });
    } else {
        console.log('geo non disponibile');
    }

    /* La Fetch API fornisce un'interfaccia moderna per ottenere risorse */
    const url = 'https://api.apixu.com/v1/forecast.json?key=';
    const key = '87d7c0c10b874d8fbfb91809192202';
    var latlong = lat + ',' + long
    var requestURL = url + key + '&q=' + latlong;
    /*+'&q='+city*/

    const controller = new AbortController()
    const signal = controller.signal

    // per prima cosa ovviamente chiede l'url del nostro file json
    var getValue = () => fetch(requestURL, {signal})
        .then(response => response.json()) //qui avviene la risposta al server e viene trasferito in un file json
        .then(res => { //al quale possiamo accedere coi vari metodi da questa seconda parte


          console.log(res.forecast.forecastday[0]);
          iconcode = res.current.condition.code;
          thislocation = res.location.tz_id;
          thistemp = res.current.temp_c + '';

          loadDayandTime(); //caricamento citt, giorno e orario

          switchSeroNuv(iconcode); //switch tra sereno e nuvoloso per il caricamento del bg

          {/* simula un orario differente
          ore = 7 ;
          alert("simulazione in corso...");
          */}

          if (ore > 5 && ore < 21) {
            giornoonotte = "giorno";
          } else {
            giornoonotte = "notte";
          }
          {/* simula sereno o nuvoloso
          serenoonuvoloso = "nuvoloso";
          alert("simulazione in corso...");
          */}
          changeBg(serenoonuvoloso, ore, giornoonotte); //cambia bg ora per ora a seconda se  sereno o nuvoloso

          {/* simula un altra citt
          thislocation = "UK/London";
          alert("simulazione in corso...");
          */}
          loadCity(); //estrapola il nome della localit dalla stringa di apixu

          {/* simula una temperatura differente
          thistemp = "-15";
          alert("simulazione in corso...");
          */}
          loadTemp(); // carica la temperatura in gradi centigradi

          {/* simula il codice icona
            iconcode = iconameteoid[0];
            alert("simulazione in corso...");
            */}
          changeMaxiIcon(iconcode);

          hideAni(); //nasconde le animazioni delle rondini e delle nuvole

        }).catch(err => { //in caso di errore stampamelo in console
            if (err.name === 'AbortError') {
                console.error('Fetch aborted')
            } else {
                console.error('Another error', err)
            }
        })
    getValue();
  });

////////////// main end //////////////////////////
//////////////////////////////////////////////////

// carica la temperatura in gradi centigradi
function loadTemp() {
  document.getElementById('temperatura').innerHTML = thistemp;
};

//estrapola il nome della localit dalla stringa di apixu e ne carica il nome
function loadCity() {
  loc = (thislocation);
  slashpos = loc.search("/")+1;
  locmod = loc.slice(slashpos);
  locdef = locmod.replace("_", " ");
  document.getElementById('citta').innerHTML = locdef;
};

  //funzione che cambia icona in base al codice meteo di apixu
function changeMaxiIcon (code) {
    var index = (iconameteoid.indexOf(code));
    switch (giornoonotte) {
      case "notte":
          document.getElementById(pageElements[4]).src = "src/img/icons/"+iconenotte[index]+".svg";
        break;
      case "giorno":
          document.getElementById(pageElements[4]).src = "src/img/icons/"+iconegiorno[index]+".svg";
        break;
      };
    };

//azione che carica le 3 skylines
function changeSkylines(array) {
  document.getElementById(pageElements[5]).src = "src/img/icons/"+array[0]+".svg";
  document.getElementById(pageElements[6]).src = "src/img/icons/"+array[1]+".svg";
  document.getElementById(pageElements[7]).src = "src/img/icons/"+array[2]+".svg";
};

//funzione che cambia sfondo a seconda dell'ora e della condizione meteo
// in aggiunta anche cambio dell'svg delle skylines
function changeBg(cond, h, gn) {
switch (gn) {
  case ("notte"):
    changeSkylines(skylinesnotte);
    loadBG(pageElements[0], bgnotte);
    break;
  case ("giorno"):
  switch (cond) {
    case ("nuvoloso"):
      if (h > 6 && h < 14) {
        changeSkylines(skylinesnuvo1);
        loadBG(pageElements[0], bgnuvo1);
      }
      else {
        changeSkylines(skylinesnuvo2);
        loadBG(pageElements[0], bgnuvo2);
      }
    break;
    case ("sereno"):
      if (h > 6 && h < 8) {
        changeSkylines(skylinesalba);
        loadBG(pageElements[0], bgalba);
      }
      if (h > 7 && h < 14) {
        changeSkylines(skylinesmatt);
        loadBG(pageElements[0], bgmatt);
      }
      if (h > 13 && h < 19) {
        changeSkylines(skylinespome);
        loadBG(pageElements[0], bgpome);
      }
      if (h > 18 && h < 21) {
        changeSkylines(skylinessera);
        loadBG(pageElements[0], bgsera);
      }
    break;
    }
  }
}

